INSERT INTO "arXiv_metadata" VALUES (5,5,'cs/0001024','2000-01-25 16:09:37','2009-11-30 17:05:47',38061,'Bernd R. Schlei','schlei@lanl.gov',23837,'tex',NULL,'A Parallel Algorithm for Dilated Contour Extraction from Bilevel Images','B. R. Schlei, L. Prasad','cs.CV','5 pages, including 3 figures. For additional detail check http://www.nis.lanl.gov/~bschlei/labvis/index.html',NULL,'Los Alamos Preprint LA-UR-00-309',NULL,'I.2.10, D.1.3, G.1.2',NULL,NULL,'  We describe a simple, but efficient algorithm for the generation of dilated contours from bilevel images. The initial part of the contour extraction is explained to be a good candidate for parallel computer code generation. The remainder of the algorithm is of linear nature.',NULL,1,1126967559,1,0);
